	public async Task<DataObjects.BooleanResponse> DeleteTenantAuto(Guid tenantId, bool trySave = true) //SampleSinglePageApplication.DataObjects+Tenant
	{
		DataObjects.BooleanResponse output = new DataObjects.BooleanResponse();
		var rec = await data.Tenants.FirstOrDefaultAsync(x => x.TenantId == tenantId);
		if (rec == null) {
			output.Messages.Add("Error Deleting Tenant " + tenantId.ToString() + " - Record No Longer Exists");
		} else {
			data.Tenants.Remove(rec);
			try {
				if (trySave) {
					await data.SaveChangesAsync();
				}
				output.Result = true;
				
				await SignalRUpdate(new DataObjects.SignalRUpdate {
					TenantId = rec.TenantId,
					ItemId = rec.TenantId.ToString(),
					UpdateType = DataObjects.SignalRUpdateType.Tenant,
					Message = "DeletedTenant"
				});
			} catch (Exception ex) {
				output.Messages.Add("Error Deleting Tenant " + tenantId.ToString() + " - " + ex.Message);
			}
		}
		return output;
	}

	public async Task<DataObjects.BooleanResponse> DeleteTenantsAuto(List<Guid> tenantIds)  //SampleSinglePageApplication.DataObjects+Tenant
	{
		DataObjects.BooleanResponse output = new DataObjects.BooleanResponse();
		var recs = await data.Tenants.Where(x => tenantIds.Any(id => id == x.TenantId)).ToListAsync();
		data.Tenants.RemoveRange(recs);
		try {
			await data.SaveChangesAsync();
			output.Result = true;
		} catch (Exception ex) {
			output.Messages.Add("Error Deleting Tenants " + string.Join(",", tenantIds.Select(o => o.ToString())) + " - " + ex.Message);
		}
		return output;
	}

