	public async Task<DataObjects.FilterTenantsAuto> GetTenantsFilteredAuto(DataObjects.FilterTenantsAuto filter, Func<IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>,IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>>? overrideFilter = null,Func<IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>,IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>>? additionalFilter = null, Func<IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>,string, bool ,IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>>? additionalOrderByAuto = null, Func<DataObjects.Tenant, DataObjects.Tenant> additionalDataAuto = null  )                    
	{
		DataObjects.FilterTenantsAuto output = filter;                                                                       
		output.ActionResponse = GetNewActionResponse();
		output.Records = null;
		
		output.Columns = new List<DataObjects.FilterColumn> {
			new DataObjects.FilterColumn{
				Align = "",                                                                                            
				Label = "Tenant Id",                                                                   
				TipText = "",                                                                                          
				Sortable = true,                                                                                         
				DataElementName = "tenantId",                                                      
				DataType = "string"                                                                                    
			},
			new DataObjects.FilterColumn{
				Align = "",                                                                                            
				Label = "Name",                                                                   
				TipText = "",                                                                                          
				Sortable = true,                                                                                         
				DataElementName = "name",                                                      
				DataType = "string"                                                                                    
			},
			new DataObjects.FilterColumn{
				Align = "",                                                                                            
				Label = "Tenant Code",                                                                   
				TipText = "",                                                                                          
				Sortable = true,                                                                                         
				DataElementName = "tenantCode",                                                      
				DataType = "string"                                                                                    
			},
			new DataObjects.FilterColumn{
				Align = "center",                                                                                      
				Label = "Enabled",                                                                   
				TipText = "",                                                                                          
				Sortable = true,                                                                                         
				DataElementName = "enabled",                                                      
				DataType = "boolean"                                                                                   
			},
		};
	
		IQueryable<Tenant> recs = data.Tenants; 
		recs = recs.Where(x => x.TenantId == output.TenantId);                                               
		if (overrideFilter != null) {
			recs = overrideFilter(recs);
		} else {
			if(additionalFilter != null) {
				recs = additionalFilter(recs);
			}
			// TODO: Guid : TenantId
			if (!string.IsNullOrWhiteSpace(filter.Name)) {
				if (filter.NameFilterExact) {
					recs = recs.Where(o => o.Name == filter.Name); // String : Name
				} else {
					recs = recs.Where(o => o.Name.Contains(filter.Name)); // String : Name
				}
			}
			if (!string.IsNullOrWhiteSpace(filter.TenantCode)) {
				if (filter.TenantCodeFilterExact) {
					recs = recs.Where(o => o.TenantCode == filter.TenantCode); // String : TenantCode
				} else {
					recs = recs.Where(o => o.TenantCode.Contains(filter.TenantCode)); // String : TenantCode
				}
			}
			if (filter.Enabled.HasValue) {
				if( filter.Enabled.Value ) {
					recs = recs.Where(o => o.Enabled == true);
				} else {
					recs = recs.Where(o => o.Enabled == false || o.Enabled == null);
				}
			}
		}

		if (!string.IsNullOrWhiteSpace(filter.Keyword)) {
			recs = recs.Where(o =>
				(filter.NameIncludeInKeyword && o.Name.Contains(string.Empty + filter.Name)) || 
				(filter.TenantCodeIncludeInKeyword && o.TenantCode.Contains(string.Empty + filter.TenantCode))
			);
		}

		bool Ascending = true;
		if (StringOrEmpty(output.SortOrder).ToUpper() == "DESC")
		{
		    Ascending = false;
		}
		
		switch (StringOrEmpty(output.Sort).ToUpper())
		{
			case "TENANTID":                                                        
				if (Ascending)
				{
					recs = recs.OrderBy(x => x.TenantId).ThenBy(x => x.TenantId);     
				}
				else
				{
					recs = recs.OrderByDescending(x => x.TenantId).ThenByDescending(x => x.TenantId);                    
				}
				break;
			case "NAME":                                                        
				if (Ascending)
				{
					recs = recs.OrderBy(x => x.Name).ThenBy(x => x.TenantId);     
				}
				else
				{
					recs = recs.OrderByDescending(x => x.Name).ThenByDescending(x => x.TenantId);                    
				}
				break;
			case "TENANTCODE":                                                        
				if (Ascending)
				{
					recs = recs.OrderBy(x => x.TenantCode).ThenBy(x => x.TenantId);     
				}
				else
				{
					recs = recs.OrderByDescending(x => x.TenantCode).ThenByDescending(x => x.TenantId);                    
				}
				break;
			case "ENABLED":                                                        
				if (Ascending)
				{
					recs = recs.OrderBy(x => x.Enabled).ThenBy(x => x.TenantId);     
				}
				else
				{
					recs = recs.OrderByDescending(x => x.Enabled).ThenByDescending(x => x.TenantId);                    
				}
				break;
		}
		if (additionalOrderByAuto != null) {
			recs = additionalOrderByAuto(recs,StringOrEmpty(output.Sort).ToUpper(),Ascending);
		}
	
		if (recs != null && recs.Count() > 0)
		{
	
			int TotalRecords = recs.Count();
			output.RecordCount = TotalRecords;
	
			if (output.RecordsPerPage > 0)
			{
				// We are filtering records per page
				if (output.RecordsPerPage >= TotalRecords)
					{
					output.Page = 1;
					output.PageCount = 1;
				}
				else
				{
					// Figure out the page count
					if (output.Page < 1) { output.Page = 1; }
					if (output.RecordsPerPage < 1) { output.RecordsPerPage = 25; }
					decimal decPages = (decimal)TotalRecords / (decimal)output.RecordsPerPage;
					decPages = Math.Ceiling(decPages);
					output.PageCount = (int)decPages;
	
					if (output.Page > output.PageCount)
					{
						output.Page = output.PageCount;
					}
	
					if (output.Page > 1)
					{
						recs = recs.Skip((output.Page - 1) * output.RecordsPerPage).Take(output.RecordsPerPage);
					}
					else
					{
						recs = recs.Take(output.RecordsPerPage);
					}
	
				}
			}
	
			// grab the id's now that it is fitered.
			var ids = await recs.Select(o => o.TenantId).ToListAsync();                                                  
			// then use the auto method to read them from the database
			var autoRecs = await this.GetTenantsAuto(ids);                                                               
	
			// loop over the sorted / ordered ids list and build the records output base on that order,
			// not the order returned by the auto recs
			List<DataObjects.Tenant> records = new List<DataObjects.Tenant>();                                           
			foreach (var id in ids)
			{
			    // find the rec out of the autos
			    var autoRec = autoRecs.Single(o => o.TenantId == id);                                                    
			    // add any addtional data we need
			    if (additionalDataAuto != null) {
					autoRec = additionalDataAuto(autoRec);
			    }
			    records.Add(autoRec);
			}
	
			output.Records = records.ToArray();
		}
	
		output.ActionResponse.Result = true;
	
		return output;
	}
	public async Task<DataObjects.Tenant> SaveTenantAuto(DataObjects.Tenant item, bool tryLookup = true, bool trySave = true, Func<IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>, DataObjects.Tenant, IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>>? lookup = null)  //SampleSinglePageApplication.DataObjects+Tenant
	{
		Tenant? rec = null;
		item.ActionResponse = GetNewActionResponse();
		if (tryLookup) {
			if (lookup == null) {
				rec = await data.Tenants.FirstOrDefaultAsync(o => o.TenantId == item.TenantId);
			} else {
				rec = await lookup(data.Tenants.AsQueryable(), item).FirstOrDefaultAsync();
			}
			if(rec != null) {
				 item.TenantId = rec.TenantId;
			}
		}
		var newRecord = false;
		if (rec == null) {
			if (item.TenantId == Guid.Empty){
				rec = new EFModels.EFModels.Tenant();
				item.TenantId = Guid.NewGuid();
				rec.TenantId = item.TenantId;
				newRecord = true;
			} else {
				item.ActionResponse.Messages.Add("Error Saving Tenant " + item.TenantId.ToString() + " - Record No Longer Exists");
				return item;
			}
		}
		item.Name = MaxStringLength(item.Name,200);
		item.TenantCode = MaxStringLength(item.TenantCode,50);

		rec.Name = item.Name; //OTHER: base(String) : actual(String)
		rec.TenantCode = item.TenantCode; //OTHER: base(String) : actual(String)
		rec.Enabled = item.Enabled; //OTHER: base(Boolean) : actual(Boolean)
		//rec.Departments = item.Departments; //OTHER: base(List`1) : actual(Department)
		//rec.DepartmentGroups = item.DepartmentGroups; //OTHER: base(List`1) : actual(DepartmentGroup)
		//rec.TenantSettings = item.TenantSettings; //OTHER: base(TenantSettings) : actual(TenantSettings)
		//rec.udfLabels = item.udfLabels; //OTHER: base(List`1) : actual(udfLabel)
		//rec.ListItems = item.ListItems; //OTHER: base(List`1) : actual(ListItem)
		//rec.ActionResponse = item.ActionResponse; //OTHER: base(BooleanResponse) : actual(BooleanResponse)

		try
		{
			if (newRecord)
			{
				data.Tenants.Add(rec);
			}
			if (trySave) {
				await data.SaveChangesAsync();
				//await SignalRUpdate(new DataObjects.SignalRUpdate
				//{
				//	//TenantId = item.TenantId,
				//	ItemId = item.TenantId.ToString(),
				//	UpdateType = DataObjects.SignalRUpdateType.Tenant,
				//	Message = "TenantSaved",
				//	Object = item
				//});
			}
			item.ActionResponse.Result = true;
		}
		catch (Exception ex)
		{
			item.ActionResponse.Messages.Add("Error Saving Tenant " + item.TenantId.ToString() + " - " + ex.Message);
		}
		return item;
	}
	public async Task<List<DataObjects.Tenant>> SaveTenantsAuto(List<DataObjects.Tenant> items, bool tryLookup = true, Func<IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>, DataObjects.Tenant, IQueryable<SampleSinglePageApplication.EFModels.EFModels.Tenant>>? lookup = null)  //SampleSinglePageApplication.DataObjects+Tenant
	{
		List<DataObjects.Tenant> output = new List<DataObjects.Tenant>();
		foreach(var item in items) {
			DataObjects.Tenant saved = await SaveTenantAuto(item, tryLookup, false, lookup);
			if (saved != null && saved.ActionResponse != null && saved.ActionResponse.Result) {
				output.Add(saved);
			}
		}
		await data.SaveChangesAsync();
		return output;
	}

