	SaveDepartmentFilterRecord(): void {
		if (!tsUtilities.HasValue(this.SavedDepartmentFilterName())) {
			tsUtilities.DelayedFocus("saved-department-filter-name");
			return;
		}
		
		let success: Function = (data: server.savedFilterDepartmentsAuto) => {
			this.MainModel().Message_Hide();
			
			if (data != null) {
				if (data.actionResponse.result) {
					let existing: savedFilterDepartmentsAuto = null;
					
					if (this.MainModel().User().savedFiltersDepartmentsAuto() != null && this.MainModel().User().savedFiltersDepartmentsAuto().length > 0) {
						existing = ko.utils.arrayFirst(this.MainModel().User().savedFiltersDepartmentsAuto(), function (item) {
							return item.savedFilterId() == data.savedFilterId;
						});
					}
					
					if (existing != null) {
						existing.Load(data);
					} else {
						let newSavedFilter: savedFilterDepartmentsAuto = new savedFilterDepartmentsAuto();
						newSavedFilter.Load(data);
						this.MainModel().User().savedFiltersDepartmentsAuto.push(newSavedFilter);
					}
					
					this.MainModel().User().savedFiltersDepartmentsAuto.sort(function (l, r) {
						return l.description() > r.description() ? 1 : -1;
					});
					
					this.GettingSavedDepartmentFilterName(false);
				} else {
					this.MainModel().Message_Errors(data.actionResponse.messages);
				}
			} else {
				this.MainModel().Message_Error("An unknown error occurred attempting to save the filter.");
			}
		};
		this.Filter().tenantId(this.MainModel().TenantId());
		
		let f: filterDepartmentsAuto = new filterDepartmentsAuto();
		f.Load(JSON.parse(ko.toJSON(this.Filter)));
		f.columns(null);
		f.records(null);
		
		let postFilter: savedFilterDepartmentsAuto = new savedFilterDepartmentsAuto();
		postFilter.description(this.SavedDepartmentFilterName());
		postFilter.savedFilterId(this.Filter().filterId());
		postFilter.userId(this.MainModel().User().userId());
		postFilter.tenantId(this.MainModel().TenantId());
		postFilter.filter(f);
		
		this.MainModel().Message_Saving();
		tsUtilities.AjaxData(window.baseURL + "api/Data/SaveSavedDepartmentFilter", ko.toJSON(postFilter), success);
	}
