	SaveTenantFilterRecord(): void {
		if (!tsUtilities.HasValue(this.SavedTenantFilterName())) {
			tsUtilities.DelayedFocus("saved-tenant-filter-name");
			return;
		}
		
		let success: Function = (data: server.savedFilterTenantsAuto) => {
			this.MainModel().Message_Hide();
			
			if (data != null) {
				if (data.actionResponse.result) {
					let existing: savedFilterTenantsAuto = null;
					
					if (this.MainModel().User().savedFiltersTenantsAuto() != null && this.MainModel().User().savedFiltersTenantsAuto().length > 0) {
						existing = ko.utils.arrayFirst(this.MainModel().User().savedFiltersTenantsAuto(), function (item) {
							return item.savedFilterId() == data.savedFilterId;
						});
					}
					
					if (existing != null) {
						existing.Load(data);
					} else {
						let newSavedFilter: savedFilterTenantsAuto = new savedFilterTenantsAuto();
						newSavedFilter.Load(data);
						this.MainModel().User().savedFiltersTenantsAuto.push(newSavedFilter);
					}
					
					this.MainModel().User().savedFiltersTenantsAuto.sort(function (l, r) {
						return l.description() > r.description() ? 1 : -1;
					});
					
					this.GettingSavedTenantFilterName(false);
				} else {
					this.MainModel().Message_Errors(data.actionResponse.messages);
				}
			} else {
				this.MainModel().Message_Error("An unknown error occurred attempting to save the filter.");
			}
		};
		this.Filter().tenantId(this.MainModel().TenantId());
		
		let f: filterTenantsAuto = new filterTenantsAuto();
		f.Load(JSON.parse(ko.toJSON(this.Filter)));
		f.columns(null);
		f.records(null);
		
		let postFilter: savedFilterTenantsAuto = new savedFilterTenantsAuto();
		postFilter.description(this.SavedTenantFilterName());
		postFilter.savedFilterId(this.Filter().filterId());
		postFilter.userId(this.MainModel().User().userId());
		postFilter.tenantId(this.MainModel().TenantId());
		postFilter.filter(f);
		
		this.MainModel().Message_Saving();
		tsUtilities.AjaxData(window.baseURL + "api/Data/SaveSavedTenantFilter", ko.toJSON(postFilter), success);
	}
